mapper.select:
SELECT rr.* FROM (SELECT ROWNUM RN, r.* FROM (SELECT a.*, b.c_t_certified,b.c_t_distance,b.c_t_fourwheel,b.c_t_maintenance,b.c_t_newcar,b.c_t_oneowner,b.c_t_rent,b.c_t_specialoption FROM (SELECT c.* FROM KCAR_CAR c LEFT JOIN KCAR_CARBRAND cb on c.cb_brand = cb.cb_brand WHERE cb.cb_domestic = '해외' ) a FULL OUTER JOIN KCAR_CAR_TAG b on a.c_num = b.c_num
WHERE cb_brand = #{brand} AND cb_m_model = #{brandModel}
WHERE cb_brand = #{brand}
ORDER BY DBMS_RANDOM.RANDOM C_T_SPECIALOPTION
ORDER BY c_year desc,c_distance asc
ORDER BY c_year asc,c_distance asc
ORDER BY c_distance asc
ORDER BY c_distance desc
ORDER BY c_price asc,c_distance asc
ORDER BY c_price desc,c_distance asc
) r ) rr WHERE RN BETWEEN ${start} AND ${end}

SELECT SUM(COUNT(c_num)) AS COUNT FROM KCAR_CAR 

	WHERE cb_brand = #{brand} AND cb_m_model = #{brandModel}


	WHERE cb_brand = #{brand}

group by cb_brand
